<?php

namespace EasyCorp\Bundle\EasyAdminBundle\Twig\Component;

use EasyCorp\Bundle\EasyAdminBundle\Config\Option\IconSet;
use EasyCorp\Bundle\EasyAdminBundle\Dto\IconDto;
use EasyCorp\Bundle\EasyAdminBundle\Provider\AdminContextProvider;

class Icon
{
    public ?string $name = null;
    private string $iconsDir = __DIR__.'/../../../assets/icons';

    public function __construct(
        private AdminContextProvider $adminContextProvider,
    ) {
    }

    public function isBuiltInIconSet(): bool
    {
        return IconSet::Custom !== ($this->adminContextProvider->getContext()?->getAssets()->getIconSet() ?? '');
    }

    public function getIcon(): IconDto
    {
        $iconSet = $this->adminContextProvider->getContext()?->getAssets()->getIconSet() ?? IconSet::FontAwesome;

        return $this->getIconDto(trim($this->name), $iconSet);
    }

    private function getDefaultIconPrefix(): string
    {
        return $this->adminContextProvider->getContext()?->getAssets()->getDefaultIconPrefix() ?? '';
    }

    private function getIconDto(string $iconName, string $appIconSet): IconDto
    {
        if (str_starts_with($iconName, IconSet::Internal.':')) {
            return $this->getInternalIcon($iconName);
        }

        if ('' !== $defaultIconPrefix = $this->getDefaultIconPrefix()) {
            $iconName = sprintf('%s:%s', $defaultIconPrefix, $iconName);
        }

        return match ($appIconSet) {
            IconSet::FontAwesome => $this->getFontAwesomeIcon($iconName),
            default => IconDto::new(name: $iconName),
        };
    }

    private function getInternalIcon(string $internalIconName): IconDto
    {
        [$iconPrefix, $iconName] = explode(':', $internalIconName);
        $iconFilePath = sprintf('%s/%s/%s.svg', $this->iconsDir, $iconPrefix, $iconName);
        if (!file_exists($iconFilePath)) {
            throw new \RuntimeException(sprintf('The icon "%s" does not exist. Check the icon name spelling and make sure that the "%s.svg" file exists in the "assets/icons/internal/ directory of EasyAdmin".', $internalIconName, $iconName));
        }

        return IconDto::new(name: $internalIconName, path: $iconFilePath, svgContents: file_get_contents($iconFilePath));
    }

    private function getFontAwesomeIcon(string $iconName): IconDto
    {
        $originalIconName = $iconName;
        // because of how legacy EasyAdmin versions work, when using FontAwesome icons
        // the names can be like 'fa fa-fw fa-file-text-o'
        // first, transform the given icon into the expected format:
        // 'fa fa-id-card' => 'fa6-regular:id-card'
        // 'far fa-id-card' => 'fa6-regular:id-card'
        // 'fa-regular fa-id-card' => 'fa6-regular:id-card'
        // 'fas fa-file-contract' => 'fa6-solid:file-contract'
        // 'fa-solid fa-file-contract' => 'fa6-solid:file-contract'
        // 'fa-brands fa-symfony' => 'fa6-brands:symfony'
        if (str_contains($iconName, 'fa6-')) {
            [$iconPrefix, $iconName] = explode(':', $iconName);
            // this is needed for compatibility with later code that also must handle icons using the old format
            $iconPrefix = (array) $iconPrefix;
        } else {
            $iconPrefix = [];
            $iconNameParts = explode(' ', $iconName);
            if (\in_array('fa-brands', $iconNameParts, true)) {
                $iconPrefix = ['fa6-brands'];
            } elseif (\in_array('fa-solid', $iconNameParts, true) || \in_array('fas', $iconNameParts, true)) {
                $iconPrefix = ['fa6-solid'];
            } elseif (\in_array('fa-regular', $iconNameParts, true) || \in_array('far', $iconNameParts, true)) {
                $iconPrefix = ['fa6-regular'];
            } else {
                // this is the case of icons such as 'fa fa-laptop'; we can't decide which
                // icon prefix to use, because sometimes it's regular and sometimes solid,
                // so we must try both
                $iconPrefix = ['fa6-solid', 'fa6-regular'];
            }

            // FontAwesome defines a lot of CSS class that don't correspond to icons, so when
            // trying to match fa-*, ignore 'fa-fw', 'fa-2x', 'fa-flip', etc.
            $iconNamePartsWithoutDesignClasses = array_filter($iconNameParts, static fn ($part) => !\in_array($part, [
                'fa-brands', 'fa-solid', 'fa-regular', 'fa-fw', 'fa-2x', 'fa-xs', 'fa-sm', 'fa-lg', 'fa-xl', 'fa-2xl',
                'fa-li', 'fa-rotate-90', 'fa-rotate-180', 'fa-rotate-270', 'fa-flip-horizontal', 'fa-flip-vertical',
                'fa-flip-both', 'fa-fade', 'fa-beat', 'fa-flip', 'fa-bounce', 'fa-shake', 'fa-spin', 'fa-border',
                'fa-pull-left', 'fa-pull-right', 'fa-1x', 'fa-2x', 'fa-4x', 'fa-stack-1x', 'fa-stack-2x',
            ], true));
            $cleanIconName = implode(' ', $iconNamePartsWithoutDesignClasses);
            preg_match('/fa-([a-z0-9-]+(?:-[a-z0-9-]+)*)/', $cleanIconName, $matches);
            $iconName = $matches[1] ?? '';
        }

        foreach ($iconPrefix as $prefix) {
            $iconFilePath = sprintf('%s/%s/%s.svg', $this->iconsDir, $prefix, $iconName);
            if (file_exists($iconFilePath)) {
                return IconDto::new(name: sprintf('%s:%s', $prefix, $iconName), path: $iconFilePath, svgContents: file_get_contents($iconFilePath));
            }
        }

        [$iconPrefix, $iconName] = $this->fixLegacyFontAwesomeIconName($iconName);
        $iconFilePath = sprintf('%s/%s/%s.svg', $this->iconsDir, $iconPrefix, $iconName);

        if (file_exists($iconFilePath)) {
            return IconDto::new(name: sprintf('%s:%s', $iconPrefix, $iconName), path: $iconFilePath, svgContents: file_get_contents($iconFilePath));
        }

        throw new \RuntimeException(sprintf('The icon "%s" does not exist. After processing by EasyAdmin, this icon corresponds to the file "%s.svg", which could not be found in any of the FontAwesome directories within the "assets/icons/" directory of EasyAdmin.', $originalIconName, $iconName));
    }

